        m_consola.append("\nPromedio de cola B:\n     "+colas[1]);
        m_consola.append("\nPromedio de cola C:\n     "+colas[2]);
        m_consola.append("\nPromedio de cola Antivirus:\n     "+colas[3]);
        m_consola.append("\nPromedio de archivos enviados por cada turno del token\n     "+promedioToken);
        m_consola.append("\nNÃºmero promedio de revisiones del antivirus por archivo\n     "+revisiones);
        

